import * as React from "react";
import { useParams } from "react-router-dom";
import { useBreakingBadContext } from "../contexts/BreakingBadContext";
import {
    Box,
    // Typography,
} from "@mui/material";

const CharacterDetail = () => {
  const params = useParams();
  const characterData = useBreakingBadContext();

  const characterInfo = characterData.characters.find((item) => {
    return item.char_id == params.characterId;
  });
  console.log("selected Character--->", characterInfo);

  return (
      <div style={{color: "white"}}>
            <h2>Full Name: {characterInfo.name}</h2>
            {/* <h1>Character Detail Page</h1> */}
            <div style={{ textAlign: "left" }}>
                    <p style={characterInfoStyle}>
                      {bull} <b>Born:</b> {character.birthday}
                    </p>
                    <p style={characterInfoStyle}>
                      {bull} <b>Nickname:</b> {character.nickname}
                    </p>
                    <p style={characterInfoStyle}>
                      {bull} <b>Portrayed By:</b> {character.portrayed}
                    </p>
                    <p style={characterInfoStyle}>
                      {bull} <b>Occupation:</b> {character.occupation[0]},{" "}
                      {character.occupation[1]}
                    </p>
                    <p style={characterInfoStyle}>
                      {bull} <b>Status:</b> {character.status}
                    </p>
                  </div>
      </div>
    );
};
export default CharacterDetail;
