import * as React from "react";
import { useParams } from "react-router-dom";
import { useBreakingBadContext } from "../contexts/BreakingBadContext";

const CharacterDetail = () => {
  const params = useParams();
  //   const { character } = props;
  const characterData = useBreakingBadContext();
  // console.log("params---->", params);
  //   console.log("params, CharacterId--->", params.characterId);
  //   console.log("data----->", characterData);

  const selectedCharacter = characterData.characters.find((item) => {
    //   console.log("id---", item.char_id);
    //   console.log("params----", params.characterId);
    return item.char_id == params.characterId;
  });
  console.log("selected-->", selectedCharacter);

  return <h1>Character Detail Page</h1>;
};
export default CharacterDetail;
